"use strict";(self.webpackChunke_census_handbook=self.webpackChunke_census_handbook||[]).push([[4806],{947:(e,a,i)=>{i.r(a),i.d(a,{assets:()=>l,contentTitle:()=>s,default:()=>h,frontMatter:()=>o,metadata:()=>r,toc:()=>d});var n=i(4848),t=i(8453);const o={id:"Chapter 03 Digital Cartographic Mapping - Case Studies",sidebar_position:2},s=void 0,r={id:"case-studies/Chapter 03 Digital Cartographic Mapping - Case Studies",title:"Chapter 03 Digital Cartographic Mapping - Case Studies",description:"back to Handbook Chapter 3",source:"@site/docs/case-studies/ch3-digital-cartographic-mapping-case-studies.md",sourceDirName:"case-studies",slug:"/case-studies/Chapter 03 Digital Cartographic Mapping - Case Studies",permalink:"/e-census-handbook/docs/case-studies/Chapter 03 Digital Cartographic Mapping - Case Studies",draft:!1,unlisted:!1,editUrl:"https://github.com/tech-acs/e-census-handbook/tree/main/docs/case-studies/ch3-digital-cartographic-mapping-case-studies.md",tags:[],version:"current",sidebarPosition:2,frontMatter:{id:"Chapter 03 Digital Cartographic Mapping - Case Studies",sidebar_position:2},sidebar:"caseStudiesSidebar",previous:{title:"Chapter 02 Planning a Digital Census - Case Studies",permalink:"/e-census-handbook/docs/case-studies/ch2-planning-digital-census-case-studies"},next:{title:"Chapter 04: Pretest and Pilot Census - Case Studies",permalink:"/e-census-handbook/docs/case-studies/ch4-pretest-and-pilot-census-case-studies"}},l={},d=[{value:"3.1 Development of digital cartographic mapping instruments and tools",id:"31-development-of-digital-cartographic-mapping-instruments-and-tools",level:2},{value:"Intended output from mapping.",id:"intended-output-from-mapping",level:3},{value:"Namibia",id:"namibia",level:4},{value:"Kenya",id:"kenya",level:4},{value:"Sierra Leone",id:"sierra-leone",level:4},{value:"Data collection forms used for mapping",id:"data-collection-forms-used-for-mapping",level:3},{value:"Kenya",id:"kenya-1",level:4},{value:"Sierra Leone",id:"sierra-leone-1",level:4},{value:"Namibia",id:"namibia-1",level:4},{value:"Senegal",id:"senegal",level:4},{value:"Software used",id:"software-used",level:3},{value:"Sierra Leone",id:"sierra-leone-2",level:4},{value:"Botswana",id:"botswana",level:4},{value:"Namibia",id:"namibia-2",level:4},{value:"Kenya",id:"kenya-2",level:4},{value:"Senegal",id:"senegal-1",level:4},{value:"3.2 Pilot mapping",id:"32-pilot-mapping",level:2},{value:"Kenya",id:"kenya-3",level:3},{value:"Namibia",id:"namibia-3",level:3},{value:"Sierra Leone",id:"sierra-leone-3",level:3},{value:"3.3 Publicity and advocacy for cartographic mapping",id:"33-publicity-and-advocacy-for-cartographic-mapping",level:2},{value:"Kenya",id:"kenya-4",level:3},{value:"Sierra Leone",id:"sierra-leone-4",level:3},{value:"Namibia",id:"namibia-4",level:3},{value:"3.4 Field data collection for cartographic mapping",id:"34-field-data-collection-for-cartographic-mapping",level:2},{value:"Kenya",id:"kenya-5",level:3},{value:"Sierra Leone",id:"sierra-leone-5",level:3},{value:"Senegal",id:"senegal-2",level:3},{value:"3.5 Map production",id:"35-map-production",level:2},{value:"Kenya",id:"kenya-6",level:3},{value:"Sierra Leone",id:"sierra-leone-6",level:3},{value:"3.6 EA Map verification",id:"36-ea-map-verification",level:2},{value:"Kenya",id:"kenya-7",level:3},{value:"Sierra Leone",id:"sierra-leone-7",level:3},{value:"3.7 Mapping personnel (recruitment, training, deployment, payments, leave/HR issues)",id:"37-mapping-personnel-recruitment-training-deployment-payments-leavehr-issues",level:2},{value:"Sierra Leone",id:"sierra-leone-8",level:3}];function c(e){const a={a:"a",blockquote:"blockquote",h2:"h2",h3:"h3",h4:"h4",li:"li",p:"p",strong:"strong",ul:"ul",...(0,t.R)(),...e.components};return(0,n.jsxs)(n.Fragment,{children:[(0,n.jsx)(a.p,{children:(0,n.jsx)(a.a,{href:"/docs/category/chapter-03-digital-cartographic-mapping",children:"back to Handbook Chapter 3"})}),"\n",(0,n.jsx)(a.h2,{id:"31-development-of-digital-cartographic-mapping-instruments-and-tools",children:"3.1 Development of digital cartographic mapping instruments and tools"}),"\n",(0,n.jsx)(a.p,{children:(0,n.jsx)(a.a,{href:"/docs/experiences-lessons-2020/chapter-03/3.1-development-of-digital-cartographic-mapping-instruments-and-tools",children:"back to Handbook section"})}),"\n",(0,n.jsx)(a.h3,{id:"intended-output-from-mapping",children:"Intended output from mapping."}),"\n",(0,n.jsx)(a.h4,{id:"namibia",children:"Namibia"}),"\n",(0,n.jsxs)(a.blockquote,{children:["\n",(0,n.jsx)(a.p,{children:"Namibia aimed to provide detailed information regarding the use of built-up structures as well as develop a Statistics Business Register and the National Dwelling Unit Frame meant to provide detailed household and land use information for each Dwelling Unit."}),"\n"]}),"\n",(0,n.jsx)(a.h4,{id:"kenya",children:"Kenya"}),"\n",(0,n.jsxs)(a.blockquote,{children:["\n",(0,n.jsx)(a.p,{children:"Kenya aimed to update her geography file for all counties and sub-counties with villages and Enumeration Areas as well as number of households."}),"\n"]}),"\n",(0,n.jsx)(a.h4,{id:"sierra-leone",children:"Sierra Leone"}),"\n",(0,n.jsxs)(a.blockquote,{children:["\n",(0,n.jsx)(a.p,{children:"Sierra Leone aimed to achieve a verified and categorized nationwide locality list of all administrative levels showing the name of each locality, district, chiefdom, and section with a unique code."}),"\n"]}),"\n",(0,n.jsx)(a.h3,{id:"data-collection-forms-used-for-mapping",children:"Data collection forms used for mapping"}),"\n",(0,n.jsx)(a.h4,{id:"kenya-1",children:"Kenya"}),"\n",(0,n.jsxs)(a.blockquote,{children:["\n",(0,n.jsx)(a.p,{children:"Two household listing forms were developed: rural homestead and household listing form (F-54-5-1A and F-54-5-1B) and urban structure and household listing form (F-54-5-2) used for collecting data in rural and urban areas respectively, both collected the GPS coordinates for each household and their elevation. In addition, there was a summary form F-54-5-1A used by village elders while F-54-5-1B used by the data collection teams to compile villages and Enumeration Areas."}),"\n"]}),"\n",(0,n.jsx)(a.h4,{id:"sierra-leone-1",children:"Sierra Leone"}),"\n",(0,n.jsxs)(a.blockquote,{children:["\n",(0,n.jsx)(a.p,{children:"The cartographic mapping questionnaire was developed to capture information on, building use, locality class, classes of roads, water bodies and expected drainage type etc. The questionnaire was mainly designed to enable the head of the household to give information about the structure and number of members in the household. Other information was obtained using observation methods by the field staff."}),"\n"]}),"\n",(0,n.jsx)(a.h4,{id:"namibia-1",children:"Namibia"}),"\n",(0,n.jsxs)(a.blockquote,{children:["\n",(0,n.jsx)(a.p,{children:"The approach to questionnaire design focused on capturing the main elements relating to the use of a building and in the case of a dwelling unit, occupants were asked the name of the head of the household, contact details, the number of rooms, and the number of usual members residing in each dwelling unit including other basic services regarding the structure they reside in, such as the source of water, electricity, and access to sanitation. The inclusion of the many stakeholder requests changed the project from a typical cartographic exercise to a statistical undertaking."}),"\n"]}),"\n",(0,n.jsx)(a.h4,{id:"senegal",children:"Senegal"}),"\n",(0,n.jsxs)(a.blockquote,{children:["\n",(0,n.jsx)(a.p,{children:"The design of the Mapping tools was a build up from versions of the previous census. They were finalized in technical workshops and bench-marking at INECV Cape Verde (for Cartography) and INSD Burkina Faso (for enumeration)."}),"\n"]}),"\n",(0,n.jsx)(a.h3,{id:"software-used",children:"Software used"}),"\n",(0,n.jsx)(a.h4,{id:"sierra-leone-2",children:"Sierra Leone"}),"\n",(0,n.jsxs)(a.blockquote,{children:["\n",(0,n.jsx)(a.p,{children:"An android-based mapping PAD  application with a data acquisition package was built by Milsat Technologies to be used for the cartographic mapping exercise to fit all the data needs in a robust manner. The application was able to prompt the mapper when it reaches a minimum threshold of an EA to be delineated. It also ensured that the mapper is within the confines of the structure wanting to collect information about as seen on the satellite imagery. The application integrated both the spatial dataset and the data attributes collected on the same platform. The application has provision to work both offline and online that ensured that work is continuous even where there is no internet coverage. These innovations made it possible to achieve a sustainable national geographic frame for Censuses and Surveys."}),"\n"]}),"\n",(0,n.jsx)(a.h4,{id:"botswana",children:"Botswana"}),"\n",(0,n.jsxs)(a.blockquote,{children:["\n",(0,n.jsx)(a.p,{children:"SmartClient for Census a WEB-based Client-Server software solution was used to collect, transmit, store and clean mapping data. SmartClient for Census enables different users to log into the system to execute tasks allocated to them and has GIS architecture with one central database for office and field work, which ensures easy access to data. This ensured that through unique workflows the data capturing was viewed and managed as soon as data was synchronized into the server and field work was ongoing. SmartClient for Census also has an online and offline functionality, that ensured that work is continuous even where there is no internet coverage."}),"\n"]}),"\n",(0,n.jsx)(a.h4,{id:"namibia-2",children:"Namibia"}),"\n",(0,n.jsxs)(a.blockquote,{children:["\n",(0,n.jsx)(a.p,{children:"The application used to collect, transmit, store and clean mapping data was designed and developed using ArcGIS -Survey 123 by Esri."}),"\n"]}),"\n",(0,n.jsx)(a.h4,{id:"kenya-2",children:"Kenya"}),"\n",(0,n.jsxs)(a.blockquote,{children:["\n",(0,n.jsx)(a.p,{children:"The Open Data Kit (ODK) a free application was used to develop and test the mapping data collection system and later the ArcGIS-Survey 123 app managed by Esri- Eastern Africa was adopted after being tested and piloted. Although ODK was easy to operate, compatible with Samsung J2 and freely available in play store, Survey 123 was introduced because it was faster to process the data in the ArcGIS environment being used in map production. Survey 123 also provided a dashboard for monitoring teams and individual MA\u2019s. After completion of a village or block the MA would edit and upload the data to the cloud server."}),"\n"]}),"\n",(0,n.jsx)(a.h4,{id:"senegal-1",children:"Senegal"}),"\n",(0,n.jsxs)(a.blockquote,{children:["\n",(0,n.jsx)(a.p,{children:"The software used for development of the RGPH-5 Mapping tools were: ARCGIS and MOBAC"}),"\n"]}),"\n",(0,n.jsx)(a.h2,{id:"32-pilot-mapping",children:"3.2 Pilot mapping"}),"\n",(0,n.jsx)(a.p,{children:(0,n.jsx)(a.a,{href:"/docs/experiences-lessons-2020/chapter-03/3.2-pilot-mapping",children:"Back to handbook section"})}),"\n",(0,n.jsx)(a.h3,{id:"kenya-3",children:"Kenya"}),"\n",(0,n.jsxs)(a.blockquote,{children:["\n",(0,n.jsx)(a.p,{children:"The technology and instruments/tools embraced were tested before the actual cartographic mapping exercise. A pilot mapping exercise was conducted to identify the most ideal tablet for use especially one with high GPS accuracy- Samsung Galaxy J2 phone was identified as having accurate GPS readings; tested the Open Data Kit (ODK) application; tested the use of Satellite imageries and aerial photographs which were used for planning and allocation of work, plotting of boundaries and features; tested the adequacy of the data collection forms; tested the suitability of the KNBS Server and entire ICT system in undertaking the digital cartographic mapping. The pilot was also used to ascertain the type of and number of vehicles to be used as well as the resources that could be required to undertake cartographic mapping in the entire country."}),"\n"]}),"\n",(0,n.jsx)(a.h3,{id:"namibia-3",children:"Namibia"}),"\n",(0,n.jsxs)(a.blockquote,{children:["\n",(0,n.jsx)(a.p,{children:"The main objective of the pilot  was to test whether the mapping tools (electronic data collection application and questionnaire) were adequate to provide the required data. This also involved testing the adequacy of logistics and administrative arrangements on the ground."}),"\n",(0,n.jsx)(a.p,{children:"The results from the pilot were used to review and improve on the mapping implementations in all operational areas. These included reviewing of tools, drawing up the final plans for the main mapping project, as well as providing the final estimation of resources required."}),"\n"]}),"\n",(0,n.jsx)(a.h3,{id:"sierra-leone-3",children:"Sierra Leone"}),"\n",(0,n.jsxs)(a.blockquote,{children:["\n",(0,n.jsx)(a.p,{children:"The pilot mapping was done in two phases and the section administrative boundary shape file was used:"}),"\n",(0,n.jsxs)(a.ul,{children:["\n",(0,n.jsx)(a.li,{children:"The first phase was done in the capital city to: assess the cartographic mapping software EA PAD 4.0 and the means of data transmission; assess the use of ARCGIS software in processing the data from EA PAD 4.0 for EA delineation; test the entire systems and processes including logistical requirements, deployments, average time spent to complete an EA in both rural and urban areas; and  identify likely problems to be faced by mappers in the field."}),"\n",(0,n.jsx)(a.li,{children:"In the second phase, the coverage was increased beyond the capital city with diversified prevailing conditions including hard to reach areas and poor internet connectivity. Three sections were randomly selected across the country."}),"\n"]}),"\n"]}),"\n",(0,n.jsx)(a.h2,{id:"33-publicity-and-advocacy-for-cartographic-mapping",children:"3.3 Publicity and advocacy for cartographic mapping"}),"\n",(0,n.jsx)(a.p,{children:(0,n.jsx)(a.a,{href:"/docs/experiences-lessons-2020/chapter-03/3.3-publicity-and-advocacy-for-cartographic-mapping",children:"Back to handbook section"})}),"\n",(0,n.jsx)(a.h3,{id:"kenya-4",children:"Kenya"}),"\n",(0,n.jsxs)(a.blockquote,{children:["\n",(0,n.jsx)(a.p,{children:"In Kenya the publicity and advocacy for the cartographic and mapping exercise was cascaded and done through the print media, radio, TV, social media and holding physical community mobilization meetings for the local administration who were in turn to hold community level sensitization meetings. Sensitization of the local administrative leadership was done by NSO staff sensitizing the local administrative officers in each county. The sensitization meetings were held, and the administrative officers were taken through the importance of census and the activities involved in the census process, inform how the village elders were to fill the mapping form 1 provided to them in the rural villages."}),"\n"]}),"\n",(0,n.jsx)(a.h3,{id:"sierra-leone-4",children:"Sierra Leone"}),"\n",(0,n.jsxs)(a.blockquote,{children:["\n",(0,n.jsx)(a.p,{children:"In Sierra Leone the Communication and Publicity division within the NSOs led the campaigns to sensitize and educate the public on the need to cooperate and support the mapping teams before the start and during the cartographic mapping process. The key tools used included Community sensitization meetings, radio discussions in both local and national languages, use of megaphones, providing local guards by the communities as part of the process to lead field staff for hard-to-reach areas and ensuring all communities are reached."}),"\n"]}),"\n",(0,n.jsx)(a.h3,{id:"namibia-4",children:"Namibia"}),"\n",(0,n.jsxs)(a.blockquote,{children:["\n",(0,n.jsx)(a.p,{children:"In Namibia a Communication Plan focusing on advocacy and publicity of the mapping was developed. The most convenient method used was the distribution of flyers and displaying of posters to create awareness. Engaged community leaders through meetings and had the opportunity to elaborate on the objectives of\nthe census mapping. Mobilization was done in each selected EA before commencement of data collection exercises to ensure that local people were aware of the project and what was expected of them. Courtesy visits to constituency and local councilors were also undertaken to introduce the exercise and request them to inform their constituency inhabitants about the exercise during their respective radio announcements and community meetings. In addition, FM Radio announcements, local newspapers, television, and specific talk shows among others to announce the commencement of fieldwork."}),"\n"]}),"\n",(0,n.jsx)(a.h2,{id:"34-field-data-collection-for-cartographic-mapping",children:"3.4 Field data collection for cartographic mapping"}),"\n",(0,n.jsx)(a.p,{children:(0,n.jsx)(a.a,{href:"/docs/experiences-lessons-2020/chapter-03/3.4-field-data-collection-for-cartographic-mapping",children:"Back to handbook section"})}),"\n",(0,n.jsx)(a.h3,{id:"kenya-5",children:"Kenya"}),"\n",(0,n.jsxs)(a.blockquote,{children:["\n",(0,n.jsx)(a.p,{children:"In Kenya, the exercise started with 12 teams with each team comprising of a team leader and 7 mapping assistants. There were also coordinators in charge of several teams. The number of teams was later increased to 40 with a total of 400 temporary Mapping Assistants (MAs) who were recruited and trained to speed up the mapping process."}),"\n",(0,n.jsx)(a.p,{children:"A team was allocated between 2 and 3 vehicles depending on the number of MAs. All teams were working either in one or two counties at a given time. A team was allocated several sub locations to map the homesteads, structures, households, and points of interest. Each MA was assigned a village or a block to work in at any given time and they would move around the entire village with the help of a village elder. The MA would use the form that had been earlier filled by the village elder to confirm the details listed as well as pick coordinates for the structures, households, and features. Data was collected using mobile phones with an in-built Global Positioning System (GPS)."}),"\n",(0,n.jsx)(a.p,{children:"Mapping for the first 27 counties was carried out using Open Data Kit (ODK) App while the rest were mapped using ArcGIS survey123."}),"\n"]}),"\n",(0,n.jsx)(a.h3,{id:"sierra-leone-5",children:"Sierra Leone"}),"\n",(0,n.jsxs)(a.blockquote,{children:["\n",(0,n.jsx)(a.p,{children:"In Sierra Leone, the actual field work started with annotation of Satellite imagery, updating of verified locality list, deployment of field staff with required logistics and delineation of EAs using the satellite imagery.\nThe Data quality assurance officers and Assistants were on the other hand busy with the required quality checks. The National quality officer was supervising all the quality assurance officers also used a customized application to ensure that data from the field mappers shows a true representation of the real information on the ground and in addition do ground truthing for randomly selected delineated EAs."}),"\n"]}),"\n",(0,n.jsx)(a.h3,{id:"senegal-2",children:"Senegal"}),"\n",(0,n.jsxs)(a.blockquote,{children:["\n",(0,n.jsx)(a.p,{children:"The cartography team included: mapping agents, team leaders, regional supervisors, and headquarters experts. Each of these had a functional mobile application under Android set up centrally for an integrated management of census operations.  The supervisor application was used to ensure coordination and monitoring of field operations. The regional supervisor application was used to manage team composition, assign work, check completeness, and send to the server. Team leader application allowed the team leaders to assign tasks to team members and coordinate field work. The cartographer agent application was used to carry out cartographic data collection on the localities."}),"\n"]}),"\n",(0,n.jsx)(a.h2,{id:"35-map-production",children:"3.5 Map production"}),"\n",(0,n.jsx)(a.p,{children:(0,n.jsx)(a.a,{href:"https://tech-acs.github.io/e-census-handbook/docs/experiences-lessons-2020/chapter-03/3.5-Map-production#35-map-production",children:"Back to handbook section"})}),"\n",(0,n.jsx)(a.h3,{id:"kenya-6",children:"Kenya"}),"\n",(0,n.jsxs)(a.blockquote,{children:["\n",(0,n.jsx)(a.p,{children:"A team of GIS technicians were recruited to support the GIS team in undertaking map production."}),"\n",(0,n.jsx)(a.p,{children:(0,n.jsx)(a.strong,{children:"Stage 1"})}),"\n",(0,n.jsxs)(a.ul,{children:["\n",(0,n.jsx)(a.li,{children:"Digitization was carried out by first downloading the spatial data collected from the cloud server. ArcGIS software was used to overlay spatial data on satellite imagery for rural areas or aerial photographs for urban areas.  The data would be symbolized by village name to determine the extent of a village. Digitization of village boundaries was guided by already generated schema."}),"\n",(0,n.jsx)(a.li,{children:"Delineation of EAs was done based on measure of size which is between 50 and 149 households making an average of 100 households. A village with more than 149 households was subdivided to create more than one EA whereas one with less than 50 households was merged with another next to it. An EA was therefore either part of a village, an entire village or several villages combined."}),"\n",(0,n.jsx)(a.li,{children:"The EA polygons were cleaned by creating topologies to show where overlaps and gaps exist. Each EA was described by its administrative and political units, type (rural or urban), status (informal or formal) and universal (special or conventional). The information was added on the EA polygons attribute table to create the census geo-file."}),"\n",(0,n.jsx)(a.li,{children:"A 13-digit code was generated from administrative units to uniquely identify each EA. Maps were prepared using templates generated during development of tools. Two maps were created, a sub-location map with all the EAs or several EAs in that sub-location and every individual EA map."}),"\n"]}),"\n",(0,n.jsx)(a.p,{children:(0,n.jsx)(a.strong,{children:"Stage 2"})}),"\n",(0,n.jsxs)(a.ul,{children:["\n",(0,n.jsx)(a.li,{children:"The maps were printed for editing guided by the editing forms and later printed for use during verification. Rural maps were printed on A3 size paper while urban maps were printed on A2 size paper."}),"\n",(0,n.jsx)(a.li,{children:"Maps returned from verification were returned to map production for the GIS technicians to effect changes from the field. After effecting the changes, final PDF maps and geo-files were forwarded by counties to uploaded on the tablets to be used during census enumeration."}),"\n",(0,n.jsx)(a.li,{children:"Hard copy maps were also printed for training and EA identification."}),"\n"]}),"\n"]}),"\n",(0,n.jsx)(a.h3,{id:"sierra-leone-6",children:"Sierra Leone"}),"\n",(0,n.jsxs)(a.blockquote,{children:["\n",(0,n.jsxs)(a.ul,{children:["\n",(0,n.jsx)(a.li,{children:"Mappers were in the field collecting data and creating temporary Enumeration Area boundaries and saving the data into the cloud. The GIS Officers and assistants who were in the respective District Offices downloaded data as sent from the field via google cloud. The EAs were digitized and delineated using ArcGIS Mapping software tool."}),"\n",(0,n.jsx)(a.li,{children:"The mapping PAD remote submission process utilizes a top-level security cloud system to scan and safely deliver uploaded tasks to GIS portal. GIS portal can only be opened by registered and verified GIS Officers."}),"\n",(0,n.jsx)(a.li,{children:"No physical maps were printed for actual census enumeration instead a Census Pad application was used with online resources like Open Street based on cartographic enumeration area boundary shapefiles, and the spatial data collected. These facilitated the census staff to locate their Enumeration areas without hard copy maps."}),"\n"]}),"\n"]}),"\n",(0,n.jsx)(a.h2,{id:"36-ea-map-verification",children:"3.6 EA Map verification"}),"\n",(0,n.jsx)(a.p,{children:(0,n.jsx)(a.a,{href:"https://tech-acs.github.io/e-census-handbook/docs/experiences-lessons-2020/chapter-03/3.6-EA-Map-verification#36-ea-map-verification",children:"Back to handbook section"})}),"\n",(0,n.jsx)(a.h3,{id:"kenya-7",children:"Kenya"}),"\n",(0,n.jsxs)(a.blockquote,{children:["\n",(0,n.jsx)(a.p,{children:"In Kenya, the exercise was undertaken by trained verification officers and coordinators who worked with the NGAO s with a collective role of confirming that all the maps and geo-file for their respective administrative units were available and that they were in their correct geographic locations and had correct spellings. During verification, several NGAOs were brought together."}),"\n",(0,n.jsx)(a.p,{children:"Additionally, the verification process identified missing features/facilities, spelling mistakes, confirmed the number of villages and boundaries, etc.  The key tools were therefore the sub-location map and geo-file. The exercise started after several EA maps had been produced for a number of counties. It was conducted in phases where in each phase several counties were covered."}),"\n",(0,n.jsx)(a.p,{children:"Verified maps and verification forms were forwarded to the map production office to effect the changes that had been documented before final production of maps. Major errors noted during verification included: spelling errors, wrong village/EA naming, incorrect EA numbering, disjointed/fragmented (Flying ) villages, omission of features, gaps where some households had not been mapped, unclear boundaries, incomplete naming of features. 32% of the EAs had one or more of the above errors."}),"\n"]}),"\n",(0,n.jsx)(a.h3,{id:"sierra-leone-7",children:"Sierra Leone"}),"\n",(0,n.jsxs)(a.blockquote,{children:["\n",(0,n.jsx)(a.p,{children:"In Sierra Leone the verification process was followed up with a ground truthing exercise. To ensure that only accredited functionaries use the App, the mapping PAD periodically runs system authentication on every phone the App is installed."}),"\n"]}),"\n",(0,n.jsx)(a.h2,{id:"37-mapping-personnel-recruitment-training-deployment-payments-leavehr-issues",children:"3.7 Mapping personnel (recruitment, training, deployment, payments, leave/HR issues)"}),"\n",(0,n.jsx)(a.p,{children:(0,n.jsx)(a.a,{href:"/docs/experiences-lessons-2020/chapter-03/3.7-mapping-personnel-recruitment-training-deployment-payments-leavehr-issues",children:"Back to handbook section"})}),"\n",(0,n.jsx)(a.h3,{id:"sierra-leone-8",children:"Sierra Leone"}),"\n",(0,n.jsxs)(a.blockquote,{children:["\n",(0,n.jsxs)(a.ul,{children:["\n",(0,n.jsx)(a.li,{children:"The Statistics Sierra Leone Councils that oversee the institution and thus responsible for the recruitment processes in the institution coordinated and supervised the recruitment process of the cartographic field staff."}),"\n",(0,n.jsx)(a.li,{children:"It all started with advertisement, shortlisting, and interview of shortlisted candidates. The recruitment was done at district level involving the local authorities and they ensured that the recruited staff were able to speak local languages, the mapping staff can communicate well among other considerations."}),"\n",(0,n.jsx)(a.li,{children:"The local authorities also supported the cartographic mapping process by recommending local guards that were to lead the mapping teams to hard-to-reach areas."}),"\n",(0,n.jsx)(a.li,{children:"Cascaded training was done starting with the Training of Trainers who were identified based on both qualification and experience after which the district level training of the field staff was conducted. At the end of the training, exams were conducted, and the best performing candidates were recruited."}),"\n",(0,n.jsx)(a.li,{children:"The cadre of personnel engaged in the Sierra Leone cartographic mapping included: Mappers mainly responsible for collecting data, updating features and delineating the EAs on the imagery; Team leaders to supervise and undertake quality checks of the mappers in the field; GIS officer to coordinate the work of the GIS Assistants at the district level; National GIS officer responsible for coordination of all digital mapping activities in the Country; National coordination team that included senior cartographers to oversee implementation of the cartographic activity in the country. He is to liaise with the regional coordinator to ensure the smooth running and successful conclusion of the exercise in the country."}),"\n",(0,n.jsx)(a.li,{children:"Quality Assurance Team (QA) to ensure that Quality Assurance/Quality Control procedures are well established and understood by members of the Census cartographic team."}),"\n"]}),"\n"]}),"\n",(0,n.jsx)(a.p,{children:(0,n.jsx)(a.a,{href:"/docs/category/chapter-03-digital-cartographic-mapping",children:"back to Handbook Chapter 3"})})]})}function h(e={}){const{wrapper:a}={...(0,t.R)(),...e.components};return a?(0,n.jsx)(a,{...e,children:(0,n.jsx)(c,{...e})}):c(e)}},8453:(e,a,i)=>{i.d(a,{R:()=>s,x:()=>r});var n=i(6540);const t={},o=n.createContext(t);function s(e){const a=n.useContext(o);return n.useMemo((function(){return"function"==typeof e?e(a):{...a,...e}}),[a,e])}function r(e){let a;return a=e.disableParentContext?"function"==typeof e.components?e.components(t):e.components||t:s(e.components),n.createElement(o.Provider,{value:a},e.children)}}}]);